import React from "react";
import {Formik, Form, Field, ErrorMessage} from 'formik'
import './CreateTask.css';
function CreateTask() {
    const now = new Date();
    const dayOfWeek = now.getDay();
    const dayOfMonth = now.getDay();
    const month = now.getMonth(); // Months are zero-based in Java Script


    const dayNames =["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    const monthNames = [
        "January", "February", "March", "April", "May", "June",
        "July", "August", "September", "October", "November", "December"
      ];


    return <div className="createTaskPage">
        <p id="day" class="date">{dayOfMonth}</p>
        <p id="month" class="date">{monthNames[month]}</p>
        <Formik>
            <Form>
                <label>Add a task:</label>
                <Field id="inputCreateTask" name="taskText" placeholder="Task: "/>
                <label>Due date:</label>
                <Field id="inputCreateTask" name="dueDate" placeholder="Due date: "/>

                <button type="submit">Add task</button>
            </Form>
        </Formik>
    </div>
}
export default CreateTask;
